import React, { useState } from "react";
import { Wheel } from "react-custom-roulette";
import axios from "axios";

const Prizes = [
  {
    option: "Better luck next time!",
    style: { backgroundColor: "#832a4a", textColor: "#e5d2da" ,fontSize: '12px !important', fontFamily: 'auto', fontWeight: 'normal' },
  },
  {
    option: "2X Savings",
    style: { backgroundColor: "#742544", textColor: "#e2d2d8" },
  },
  {
    option: "â‚¹100 Cashback",
    style: { backgroundColor: "#551b31", textColor: "#e9e1e4" },
  },
  {
    option: "â‚¹20 ðŸ’¸",
    style: { backgroundColor: "#65203a", textColor: "#e9dfe3" },
  },
  {
    option: "â‚¹50 ðŸ’¸",
    style: { backgroundColor: "#832a4a", textColor: "#e5d2da" },
  },
  {
    option: "1.5X Savings",
    style: { backgroundColor: "#742544", textColor: "#e2d2d8" },
  },
  {
    option: "2X Savings",
    style: { backgroundColor: "#551b31", textColor: "#e9e1e4" },
  },
  {
    option: "â‚¹50 ðŸ’¸",
    style: { backgroundColor: "#65203a", textColor: "#e9dfe3" },
  },
];

export default function Spinner() {
  const [mustSpin, setMustSpin] = useState(false);
  const [prizeNumber, setPrizeNumber] = useState(0);
  const [access_token, setAccess_token] = useState(
    process.env.REACT_APP_fampay_access_token
  );
  // Id of Google Spreadsheet which is used to store data
  const spreadsheetId = process.env.REACT_APP_fampay_spreadsheetId;

  const apiCall = () => {
    // Request Access Token from oAuth 2.0
    axios
      .post(`https://accounts.google.com/o/oauth2/v2/auth`, null, {
        params: {
          scope: "https://www.googleapis.com/auth/spreadsheets",
          include_granted_scopes: true,
          response_type: "token",
          state: "state_parameter_passthrough_value",
          redirect_uri: "https://fampay-spinner.herokuapp.com",
          client_id: process.env.REACT_APP_fampay_client_id,
        },
      })
      .then(
        (response) => {
          console.log(response);
          // setAccess_token(response);
        },
        (error) => {
          console.log(error);
        }
      );
    // Post Data to Google Spreadsheet
    var pwa = navigator.userAgentData.mobile ? "mobile-pwa" : "web-pwa";
    var data = {
      majorDimension: "ROWS",
      values: [[pwa, new Date().toISOString(), prizeNumber]],
    };
    axios
      .post(
        `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/A1:append`,
        data,
        {
          params: {
            includeValuesInResponse: false,
            insertDataOption: "INSERT_ROWS",
            responseDateTimeRenderOption: "SERIAL_NUMBER",
            responseValueRenderOption: "FORMATTED_VALUE",
            valueInputOption: "USER_ENTERED",
            access_token: access_token,
          },
        }
      )
      .then(
        (response) => {
          console.log(response);
        },
        (error) => {
          console.log(error);
        }
      );
  };

  const handleSpinStop = () => {
    setMustSpin(false);

  };

  const handleSpinClick = () => {

    const newPrizeNumber = Math.floor(Math.random() * Prizes.length);
    console.log(newPrizeNumber);

    setPrizeNumber(newPrizeNumber);
    setMustSpin(true);
    apiCall();
  };

  return (
    <>
      <div className="container">
        <Wheel
          mustStartSpinning={mustSpin}
          prizeNumber={prizeNumber}
          data={Prizes}
          fontSize={15}
          outerBorderColor={"#dad9d6"}
          radiusLineColor={"#65203a"}
          onStopSpinning={handleSpinStop}
        />
      </div>

      <button id="spinbutton" onClick={handleSpinClick}>
        <span>Spin</span>
      </button>

      <div>Power Bar</div>

      <div className="container card">
        <h2 >Spin the wheel now to get rewarded</h2>
        <p style={{ fontSize: "1rem", color: "#7d7d7d" }}>
          Tap on Spin or rotate the wheel anti-clockwise and release to start
          spinning
        </p>
      </div>
      <a  style={{ color: "#e3ef31f0", textDecoration:'none', fontWeight:'bold' }} href="https://www.npmjs.com/package/react-custom-roulette" target="_blank">
        Have a question? Get Help
      </a>
    </>
  );
}
